function _toConsumableArray(arr) { if (Array.isArray(arr)) { for (var i = 0, arr2 = Array(arr.length); i < arr.length; i++) { arr2[i] = arr[i]; } return arr2; } else { return Array.from(arr); } }

angular.module("app").controller("characterDetailsController", ["characterComicsService", "characterSeriesService", "getCharacterNameByIdService", "$stateParams", characterDetailsController]);
function characterDetailsController(characterComicsService, characterSeriesService, getCharacterNameByIdService, $stateParams) {
    var vm = this;
    vm.characterComics = [];
    vm.offsetComics = 0;
    vm.totalCharacterComics = 0;

    vm.characterSeries = [];
    vm.offsetSeries = 0;
    vm.totalCharacterSeries = 0;

    vm.limit = 18;

    vm.characterId = $stateParams.characterId;
    vm.characterInfos = [];

    vm.getCharacterComics = function (reset) {
        characterComicsService.getAllCharacterComics(vm.characterId, vm.offsetComics).then(function (response) {
            vm.totalCharacterComics = response.data.data.total;
            if (reset) {
                vm.characterComics = response.data.data.results;
            } else {
                vm.characterComics = [].concat(_toConsumableArray(vm.characterComics), _toConsumableArray(response.data.data.results));
            }
        }).catch(function (error) {
            console.log(error);
        });
    };

    vm.getCharacterSeries = function (reset) {
        characterSeriesService.getAllCharacterSeries(vm.characterId, vm.offsetSeries).then(function (response) {
            vm.totalCharacterSeries = response.data.data.total;
            if (reset) {
                vm.characterSeries = response.data.data.results;
            } else {
                vm.characterSeries = [].concat(_toConsumableArray(vm.characterSeries), _toConsumableArray(response.data.data.results));
            }
        }).catch(function (error) {
            console.log(error);
        });
    };

    vm.getCharacterName = function () {
        getCharacterNameByIdService.getCharacterNameById(vm.characterId).then(function (response) {
            vm.characterInfos = response.data.data.results[0];
        }).catch(function (error) {
            console.log(error);
        });
    };

    vm.search = function () {
        vm.offsetComics = 0;
        vm.offsetSeries = 0;
        vm.getCharacterComics(true);
        vm.getCharacterSeries(true);
    };

    vm.verMaisComics = function () {
        vm.offsetComics += vm.limit;
        vm.getCharacterComics();
    };

    vm.verMaisSeries = function () {
        vm.offsetSeries += vm.limit;
        vm.getCharacterSeries();
    };

    vm.goProTopo = function () {
        window.scrollTo({
            top: 0,
            behavior: "smooth"
        });
    };

    vm.getCharacterName();
    vm.search();
};
"use strict";

function _toConsumableArray(arr) { if (Array.isArray(arr)) { for (var i = 0, arr2 = Array(arr.length); i < arr.length; i++) { arr2[i] = arr[i]; } return arr2; } else { return Array.from(arr); } }

angular.module("app").controller("homeController", ["charactersService", "$state", homeController]);

function homeController(charactersService, $state) {
    var vm = this;
    vm.characters = [];
    vm.offset = 0;
    vm.limit = 20;
    vm.totalCharacters = 0;
    vm.characterNotFound = false;
    vm.unsearched = true;

    vm.search = function () {
        vm.offset = 0;
        vm.getCharacters(true);
    };

    vm.getCharacters = function (reset) {
        charactersService.getAllCharacters(vm.characterName, vm.offset).then(function (response) {
            vm.totalCharacters = response.data.data.total;
            if (reset) {

                vm.characters = response.data.data.results;

                if (response.data.data.total > 0) {
                    vm.characterNotFound = false;
                } else {
                    vm.characterNotFound = true;
                }
            } else {
                vm.characters = [].concat(_toConsumableArray(vm.characters), _toConsumableArray(response.data.data.results));
            }
            vm.unsearched = false;
        }).catch(function (error) {
            console.log(error);
        });
    };

    vm.verMais = function () {
        vm.offset += vm.limit;
        vm.getCharacters();
    };

    vm.seeDetails = function (character) {
        $state.go("details", { characterId: character.id });
    };

    vm.goProTopo = function () {
        window.scrollTo({
            top: 0,
            behavior: "smooth"
        });
    };
}
